import React from "react";
import ReactMapGL from "react-map-gl";
import {Marker} from "react-map-gl";
import firebase from '../firebase';
import { useState } from "react";
import './marker.css';

export default async function Map() {
  var [viewport, setViewport] = useState({
    latitude: 37.78,
    longitude: -122.41,
    width: "100vw",
    height: "100vh",
    zoom: 4,
  });

  const ref = firebase.database().ref().orderByKey();
  let afis = await ref.once("value")
                      .then((snap) => {
                        snap.map((child) => {
                          return(
                            <Marker
                              key={child.key}
                              latitude={child.val().x}
                              longitude={child.val().y}
                            > 
                              <img src = ".\mapbox-icon (1).png"></img>
                            </Marker>
                          );
                        });
                      })
  

  
  
  return (
    <>
      <ReactMapGL
        className="map"
        {...viewport}
        mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
        mapStyle="mapbox://styles/dogewithit/ckfs5obbg1tas19nrt6ryulci"
        onViewportChange={(viewport) => {
          setViewport(viewport);
        }}>
        {
          afis
        }
      </ReactMapGL>
    </>
  );
}
