import React from "react";
import ReactMapGL from "react-map-gl";
import {Marker} from "react-map-gl";
import firebase from '../firebase';
import { useState } from "react";
import './marker.css';

export default function Map() {
  var [viewport, setViewport] = useState({
    latitude: 37.78,
    longitude: -122.41,
    width: "100vw",
    height: "100vh",
    zoom: 4,
  });

  const ref = firebase.database().ref().orderByKey();
  let coords = [];    
  ref.once("value")
      .then((snap) => {
          snap.forEach((child) => {
              coords.push([child.val().x, child.val().y]);
          });
      })
  console.log(coords);
  const afis = coords.map(value => {
    console.log(value);
    return(<div className="marker">
      <Marker
        longitude={value[0]}
        latitude={value[1]}
      >
        <img src = "src\Components\Map\mapbox-icon (1).png"></img>
      </Marker>
    </div>)
  });

  console.log(afis);
  return (
    <>
      <ReactMapGL
        className="map"
        {...viewport}
        mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
        mapStyle="mapbox://styles/dogewithit/ckfs5obbg1tas19nrt6ryulci"
        onViewportChange={(viewport) => {
          setViewport(viewport);
        }}>
        {
          afis
        }
      </ReactMapGL>
    </>
  );
}
