import React from "react";
import ReactMapGL from "react-map-gl";
import {Marker} from "react-map-gl";
import firebase from '../firebase';
import { useState } from "react";
import './marker.css';

export default function Map() {
  var [viewport, setViewport] = useState({
    latitude: 37.78,
    longitude: -122.41,
    width: "100vw",
    height: "100vh",
    zoom: 4,
  });

  const ref = firebase.database().ref().orderByKey();
  let coords = [];    
  ref.once("value")
      .then((snap) => {
          snap.forEach((child) => {
              coords.push([child.val().x, child.val().y]);
          });
      })
  return (
    <>
      <ReactMapGL
        className="map"
        {...viewport}
        mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
        mapStyle="mapbox://styles/dogewithit/ckfs5obbg1tas19nrt6ryulci"
        onViewportChange={(viewport) => {
          setViewport(viewport);
        }}>
      </ReactMapGL>
      {
        <Marker
              key={1}
              coordinates={[coords[0][0], coords[0][1]]}
              anchor="bottom"
            >
              <img className="marker" src="https://lh3.googleusercontent.com/proxy/hvAf782JDVhCxmuuwhKkTJ4y2mvZHjNvVBF-Xvy9jnJrS56UBGT5a_riehMgeZ_4ZRaHOweYOBx45I6XHiUszRhORqo6GZfwLDXKCnUl5ZEi"></img>  
            </Marker>
      }
    </>
  );
}
